const toggle=document.querySelector(".toggle"),menu=document.querySelector(".menu");function toggleMenu(){menu.classList.contains("active")?(menu.classList.remove("active"),toggle.querySelector("a").innerHTML="<i class=’fas fa-bars’></i>"):(menu.classList.add("active"),toggle.querySelector("a").innerHTML="<i class=’fas fa-times’></i>")}toggle.addEventListener("click",toggleMenu,!1);